import React, { Component } from 'react'
import { View } from 'react-native';
import { Image, Text } from 'react-native-elements';
import Constance from '../theme/const';
import Anim from './anim';

interface props {
     json: any,
     caption: String
}
const Bording = (props: props) => {
     const { json, caption } = props;
     return (
          <View style={{ justifyContent: 'center', alignItems: 'center', marginHorizontal: 25, height: 230, borderRadius: 12 }}>

               <View style={{ backgroundColor: Constance.Black, opacity: 0.8, height: '100%', width: '100%', borderRadius: 12 }}>
               </View>

               <View style={{ height: '100%', width: '100%', justifyContent: 'center', alignItems: 'center', padding: 10, position: 'absolute' }}>
                    <View style={{ justifyContent: 'center', alignItems: 'center', }}>
                         <Anim json={json} autoplay={true} autosize={true} loop={true} speed={1} style={{ height: '90%', width: '90%' }} />
                    </View>
                    <Text style={{ bottom:20, color: Constance.dark_text, fontFamily: 'SourceSerifPro-BoldItalic', fontSize: Constance.medium, fontWeight: '900', textAlign: 'center', marginHorizontal: 40, marginVertical: 10 }}>{caption}</Text>
               </View>

          </View>
     )
}

export default Bording;
