import React, { Component } from 'react'
import { View } from 'react-native';
import { Image, Text } from 'react-native-elements';
import Constance from '../theme/const';

interface props{
image:any,
caption:String
}
const Bording =(props: props)=> {
    const{image, caption} = props;
    return (
      <View style={{justifyContent:'center', alignItems:'center', marginHorizontal:25, height:230, borderRadius:12}}>
           
           <View style={{ backgroundColor:Constance.Black, opacity:0.8, height:'100%', width:'100%',borderRadius:12}}>
                </View>

           <View style={{justifyContent:'center', alignItems:'center', padding: 10, position:'absolute'}}>
            <Image source={image} style={{height:130, width:190}}/>
            <Text style={{color:Constance.dark_text,fontFamily:'SourceSerifPro-SemiBoldItalic' , fontSize:Constance.medium, fontWeight:'900',textAlign:'center', marginHorizontal:40 , marginVertical:10}}>{caption}</Text>   
      </View>      
     
     </View>
    )
}

export default Bording;
