import React, { Component, useState } from 'react'
import { FlatList, SafeAreaView, StatusBar, Text, View } from 'react-native'
import { Icon, Image } from 'react-native-elements'
import PagerView, { PagerViewOnPageSelectedEvent } from 'react-native-pager-view'
import ButtonComponent from '../components/button'
import Bording from '../components/onboarding'
import style from '../styles/screens/onboarding'
import Constance from '../theme/const';


const OnBoarding = ({navigation}) => {

  const [currentPage, setCurrentPage] = useState<number>(0);
  const [key, setKey] = useState<number>(1);

    return (
        <View >

<StatusBar barStyle={'light-content'}/>
            <Image source={require('../../assets/images/onboardingimage.png')}
                style={style.image}
                resizeMode="stretch" />

            <View style={style.container}  >
            </View>

            <View
                style={style.containerTop}  >

<PagerView
          initialPage={0}
          scrollEnabled
          style={{ flex: 1 }}
          showPageIndicator={true}
          onPageSelected={(e: PagerViewOnPageSelectedEvent) => {
            setCurrentPage(e.nativeEvent.position);
          }}
        >
          <View key="1" style={{ justifyContent: "center" }}>
            <Bording
              image={require('../../assets/images/booking.png')}
              caption={
                "Browse availabe hotes in your area"
              }
            />
          </View>
          <View key="2" style={{ justifyContent: "center" }}>
            <Bording
              image={require('../../assets/images/content.png')}
              caption={
                "Book a room at your favourite hotel"
              }
            />
          </View>
          <View key="3" style={{ justifyContent: "center" }}>
            <Bording
              image={require('../../assets/images/organizer.png')}
              caption={
                "Manage your stay"
              }
            />
          </View>
        </PagerView>
                   

                  
                    <View
                style={style.header}  >
                <View style={{backgroundColor: currentPage >= 0 ? Constance.White : Constance.Grey,
                    flexGrow: 1,
                     height:4,
                      borderRadius:4, marginHorizontal:2}}>

                </View>
                <View style={{backgroundColor:currentPage >= 1 ? Constance.White : Constance.Grey,
                flexGrow: 1,
                     height:4, borderRadius:4, marginHorizontal:2}}>

                </View>
                <View style={{backgroundColor:currentPage >= 2 ? Constance.White : Constance.Grey, 
                     flexGrow: 1, height:4, borderRadius:4, marginHorizontal:2}}>

                </View>

                <View style={{width:40, borderRadius:4}}>
                    <Icon type='ionicon' 
                    name='chevron-forward-outline' 
                    size={34} 
                    color={Constance.dark_text} 
                    onPress={()=> navigation.navigate("loginscreen")}/>
                </View>

            </View>
                </View>
           
        </View>
    )
}

export default OnBoarding
