import React, { Component, useContext } from 'react';
import { View, Text, StyleSheet, Dimensions, Image, Button, Switch, StatusBar } from 'react-native';
import {ThemeContext}  from '../theme/themeProvider';
import styles from '../styles/components/card';

const Card = () => {
const { theme, dark, toggle } = useContext(ThemeContext);

return (
   <View style={[styles.container,{ backgroundColor: theme.background }]} >
       <StatusBar barStyle={'dark-content'} />
      <View style = {styles.containerSwitch}>
       <Switch
         trackColor={{ false: "#767577", true: "#ccc" }}
         thumbColor={dark ? "#fff" : "#f4f3f4"}
         onChange={toggle} value = {dark} />
     </View>
     <View style={[styles.containerCard, { backgroundColor: theme.background }]}>
        <Image style={styles.img}
              source={{ uri: 'https://images.unsplash.com/photo-1575908906110-6be79061f273?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1923&q=80' }} />
            <Text adjustsFontSizeToFit={true} style={[styles.text, { color: theme.text }]}>
          Nature, in the broadest sense, is the natural, physical, or material world or universe.
         "Nature" can refer to the phenomena of the physical world, and also to life in general.
         The study of nature is a large, if not the only, part of science.
            </Text>
       </View>
    </View>
   );
}


export default Card;
